<!-- 此文档为组件 -->
<script>
export default{
  data(){   // 组件的property
    return{
      num:0,
      uname:'Ginlon',
      msg:'<h2>标题</h2>',
      id:"d1",
      url:'data:image/jpeg;base64,',
      attributeName:'id',
      mouseEvent:'click',
      message:'hi',
      user:{
        name:'Ginlon',
        age:18,
        sex:'man'
      }
    }
  },
  // vue还通过组件实例暴露了一些内置property，如$attrs和$emit,都有$前缀以作区分 
  methods:{ // 定义方法
    changeUname:function(){
      // this指向当前的vue实例
      this.uname = 'Dragon'
    },
    changeID:function(){
      this.id='d2'
    },
    reverseMessgae:function(){
      console.log('methods') 
      return this.message.split('').reverse().join('')
    }
  },
  computed:{// 计算属性，只要依赖值不变，那么不会重新计算，计算属性将基于他们的响应关系缓存，提高性能 this.author.books、
  // 简写版
    // reverseMsg:function(){
    //   console.log('计算属性'); 
    //   return this.message.split('').reverse().join('')
    // }
  // 完整计算属性写法
    reverseMsg:{
      get:function(){
        return this.message.split('').reverse().join('')
      },
      set:function(newValue){ // 计算属性一般是没有set方法，计算属性是只读属性
        console.log(newValue)
        this.message = newValue  
      }
    }
  },
  watch:{ // 监听数据变化，watch允许我们执行异步操作（访问API），并设置一个执行该操作的条件，这些都是计算属性无法做到的
  // 函数写法
    // message:function(newValue,oldValue){
    //   console.log(newValue)
    //   console.log(oldValue)
    //   if(newValue.length < 5 || newValue.length > 10){
    //     console.log('输入内容长度在5到10之间')
    //   }
    // }
  
  // 对象写法
    message:{
      immediate:true, // 表示初始化时立刻调用handler方法
      handler:function(newValue,oldValue){
        console.log(newValue)
        console.log(oldValue)
        if(newValue.length < 5 || newValue.length > 10){
          console.log('输入内容长度在5到10之间')
        }
      }
    },
    // user:function(newValue){// watch可以监听数据，但监听不到对象的属性变化，需要使用深度监听
    //   console.log(newValue)
    // },

    // user:{// 深度监听
    //   handler:function(newValue){
    //     console.log(newValue)
    //   },
    //   deep:true, // 表示是否深度监听，侦听器会一层层向下遍历，给对象每个属性都加上侦听器
    // },
    "user.name":{ // 使用字符串形式进行优化，只会单独监听对象中对应的属性
      handler:function(newValue){
        console.log(newValue)
      },
      deep:true, // 表示是否深度监听，侦听器会一层层向下遍历，给对象每个属性都加上侦听器
    }
  }
}
</script>

<template>  <!-- 组件的模板 -->
<div>
  <p>{{num}}</p>  <!-- mustache语法{{}} -->
  <p>{{uname}}</p>
  <!-- v-ones：使用v-ones插值不会更新 -->
  <p v-once >{{num}}</p>
  <p v-once >{{uname}}</p>
  <button @click="changeUname">改变名字</button>
  <!-- v-html 识别html语法 -->
  <p>{{msg}}</p>
  <p v-html="msg"> </p>
  <!-- v-bind: 动态绑定属性的内容 -->
  <p v-bind:id="id">v-bind绑定</p>
  <img v-bind:src="url">
  <button @click="changeID">改变ID</button>
  <button @click="id='d2'">改变ID</button>
  <!-- 使用javascript表达式 -->
  <p>{{num+1}}</p>
  <p>{{uname.split('').reverse().join('' )}}</p>
  <!-- 语法糖 ‘v-bind:’ 可以用 ‘:’ 代替-->
  <p :id="id">v-bind绑定</p> 
  <!-- v-on 用于监听DOM事件 -->
  <button v-on:click="changeID">改变ID</button>
  <!-- v-on 语法糖 @ -->
  <button @click="changeID">改变ID</button>
  <!-- 动态参数 -->
  <!-- 动态属性 -->
  <p v-bind:[attributeName]='id'>v-bind绑定</p>
  <button @click="attributeName='class'">改变属性</button>
  <!-- 动态事件 -->
  <button @[mouseEvent]="attributeName = 'class' ">动态事件</button>
  <button @click="mouseEvent='mouseout'">改变事件</button>
  <!-- 三种输出方法比较 --> 
  <!-- 第一种 js表达式，总共会计算三次 -->
  <p>{{message}}</p>
  <p>{{message.split('').reverse().join('')}}</p>
  <p>{{message.split('').reverse().join('')}}</p>
  <p>{{message.split('').reverse().join('')}}</p>
  <!-- 第二种 使用计算属性，总共会计算一次 -->
  <p>{{reverseMsg}}</p>
  <p>{{reverseMsg}}</p>
  <p>{{reverseMsg}}</p>
  <!-- 第三种 使用方法，总共会执行三次 -->
  <p>{{reverseMessgae()}}</p>
  <p>{{reverseMessgae()}}</p>
  <p>{{reverseMessgae()}}</p>
  <!-- 改变依赖值后计算属性会重新计算 -->
  <button @click="message = '你好'">改变message</button>
  <button @click="reverseMsg = '你好'">计算属性set方法</button>
  <!-- watch监听器 -->
  <p>{{message}}</p>
  <button @click="message='你好'">改变message</button>
  <!-- 监听器案例; v-model数据的双向绑定 -->
  <input type="text" v-model="message">
  <!-- 深度监听案例 -->
  <p>深度监听</p>
  <p>{{user.name}}</p>
  <button @click="user.name='Dragon'">改变名字</button>
</div>
</template>

